<?phpnamespace Sts\Models;// Redirecionar ou para o processamento quando o usuário não acessa o arquivo index.phpif(!defined('R8P3B1R9S6L1')){    //header("location:/");    die("Erro: Pagina nao encontrata");}/** * Models responsável em buscar os dados da página contato * * @author Daniel Canedo */class StsContentContact{    /** @var array $data Recebe os registros do banco de dados */    private array|null $data;     /**     * Instancia a classe genérica no helper responsável em buscar os registro no banco de dados.     * Possui a QUERY responsável em buscar os registros no BD.     * @return array|null Retorna o registro do banco de dados com informações para página Contato     */    public function index(): array|null    {          $viewContentContactTop = new \Sts\Models\helper\StsRead();        $viewContentContactTop->fullRead("SELECT image, title, content FROM sts_contacts_top WHERE id=:id LIMIT :limit", "id=1&limit=1");        $this->data['top']= $viewContentContactTop->getResult();        $viewContentContactleft = new \Sts\Models\helper\StsRead();        $viewContentContactleft->fullRead("SELECT title, sub_title, icon_red_soc_1, url_red_soc_1, nome_red_soc_1, icon_red_soc_2, url_red_soc_2, nome_red_soc_2, title_contacts, icon_contacts, contact_1, tel_1, contact_2, tel_2, contact_3, tel_3, contact_4, tel_4, contact_5, tel_5, contact_6, tel_6, title_horario, content_horario, icon_tel, title_tel, tel_geral FROM sts_contacts_left WHERE id=:id LIMIT :limit", "id=1&limit=1");        $this->data['content_left']= $viewContentContactleft->getResult();        $viewContentContactRight = new \Sts\Models\helper\StsRead();        $viewContentContactRight->fullRead("SELECT assunto, nome, email, telefone, mensagem, btn_test  FROM sts_contacts_right WHERE id=:id LIMIT :limit", "id=1&limit=1");        $this->data['content_right']= $viewContentContactRight->getResult();                  //echo('<pre>');print_r($this->data['content_right']); echo('</pre>');        return $this->data;    }}?>