INSERT INTO `sts_solutions_acd` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `image_1`, `text_image_1`, `image_2`, `text_image_2`, `image_3`, `text_image_3`, `image_4`, `text_image_4`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_center`, `text_btn_center`, `title_under`, `text_under`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'academia.jpg', 'Gestão Completa', 'e treinos na palma da mâo', 'O Secullum Academia', 'Torna a gestão da sua academia muito mais simples e eficiente com um sistema de gerenciamento completo e\r\n                        um aplicativo exclusivo para alunos e professores, ideal para as academias que querem se manter atualizadas\r\n                        e proporcionar uma experiência moderna e de mais praticidade para alunos e professores. <br> <br> <br>\r\n                        Assista ao vídeo e veja como tornar seu negócio mais atrativo com o Secullum academia.', 'contato?assunto=orcamento_academia', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/Bd4ZoNtnn1E\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'imagem_academia_1.jpg', 'Todas as Informaçoes na palma de sua mão', 'imagem_academia_2.jpg', 'Todas as Informaçoes na palma de sua mão', 'imagem_academia_3.jpg', 'Todas as Informaçoes na palma de sua mão', 'imagem_academia_4.jpg', 'Todas as Informaçoes na palma de sua mão', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e\r\n                                <br>Smartphone, completo para quem procura otimizar o <br>\r\n                                controle de acesso com um baixo investimento.\r\n                                <br><br>\r\n                                É capaz de identificar pessoas através de <br> Reconhecimento Facial, Senhas Numéricas ou QR Code.', 'soluche', 'Saiba Mais', 'Características do Secullum Academia', 'O aplicativo do Secullum academia foi criado para oferecer mais facilidades ao alunos da academia, <br>\r\n                        provendo todas as informações de treinos, evolução corporal e financeiro através do uso de Smartphones. <br>\r\n                        Para os professores, com um Tablet é possível acompanhar os alunos e ter à mão todas as informações sem a\r\n                        necessidade de impressões e com total mobilidade.', 'contato?assunto=orcamento_academia', 'Solicitar Orçamento', 1, '2023-04-12 10:50:22', NULL);
INSERT INTO `sts_solutions_aces` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `sub_title_left_1`, `text_left_1`, `sub_title_center_1`, `text_center_1`, `sub_title_right_1`, `text_right_1`, `title_center_2`, `sub_title_left_2`, `text_left_2`, `sub_title_center_2`, `text_center_2`, `sub_title_right_2`, `text_right_2`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_image_center`, `test_btn_image_center`, `title_under`, `text_under_1`, `text_under_2`, `text_under_3`, `text_under_4`, `link_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'acesso.jpg', 'Controle de Acesso', 'Versátil e Descomplicado', 'O Secullum Acesso', 'É um sistema criado para o controle inteligente do fluxo de pessoas, com gestão por horário, escala, níveis de acesso e ambientes,\r\n                        bloqueando entradas e saídas indevidas e até mesmo a lotação dos ambientes.\r\n                        O sistema conta com todas as funcionalidades de monitoramento em tempo real, relatórios para análise, liberação de\r\n                        acesso por senha, código de barras, proximidade, MiFare, função pânico, integração com CFTV, entre outras aplicações.\r\n                        Além disso possui diversas funcionalidades como \"Função Pânico\", permite a geração de relatórios em diversos formatos\r\n                        e conta com um aplicativo exclusivo onde você pode liberar acessos, agendar visitantes, reservar ambientes, compartilhar\r\n                        credenciais de acesso com seus visitantes e muito mais!\r\n                        <br> Experimente o Secullum Acesso e tenha muito mais segurança!', 'contato?assunto=orcamento_acesso', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/xrDi4k02Vtk\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Gerenciamento de Entradas e Saídas com Segurança', 'O sistema Secullum Acesso controla e gerência a circulação em locais privados como empresas e condomínios, garantindo segurança aos usuários.', 'Saiba Quem, Quando e Onde Acessou', 'O Secullum Acesso permite controlar o fluxo de pessoas por meio de horário, escalas, níveis de acesso e ambientes. Bloqueia entradas e saídas não autorizadas em locais ou horários específicos.', 'Secullum Acesso App', 'O Secullum Acesso App chegou para reforçar e simplificar o fluxo de pessoas em empresas e condomínios residenciais ou empresariais.\r\n                        Com ele e possível liberar entradas e saídas, consultar históricos de acessos, cadastrar e agendar visitantes e reservar espaços, como\r\n                        sala de reuniões, salão de festas, quadras esportivas, entre outros locais, que podem ser personalizados.', 'Módulos Adicionais do Secullum Acesso', 'Módulo Recepção', 'Registra o cadastro de visitantes na empresa, além de possibilitar o acesso às informações úteis\r\n                        para às atividades da recepção.', 'Módulo Pessoal', 'Efetua agendamento prévio de visitas, consulta acessos, faz liberações extras.', 'Módulo Refeitório', 'Registra o acesso ao refeitório, com controle de cobrança, relatório de pôr tipo de refeição e\n                        integração com o Sistema de Ponto.', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e\r\n                                <br>Smartphone, completo para quem procura otimizar o <br>\r\n                                controle de acesso com um baixo investimento.\r\n                                <br><br>\r\n                                É capaz de identificar pessoas através de <br> Reconhecimento Facial, Senhas Numéricas ou QR Code.', 'soluche', 'Saiba Mais', 'Características do Secullum Acesso', '<strong>Relatórios: </strong>\r\n                        São vários relatórios disponíveis, em formatos diversificados, atendendo todas as necessidades. Distribuição de Horários,\r\n                        Pessoas Presentes, Ocorrências, Etiquetas, Relatórios Personalizados, Números Provisórios, Números Não Encontrados, Vários Números de Pessoas,\r\n                        Liberações Extras, Bloqueios Extras, Resumo de Acessos, Acesso Pessoal, Acesso Diário, Acesso por Equipamento, Estatísticas Inconformidades,\r\n                        Relatório de Visitas, Último Acesso, Liberações Manuais de Catracas, Histórico de Números Usados, Histórico de Classificações,\r\n                        Histórico de Níveis, Histórico de Estados.', '<strong>Biometrias: </strong>\r\n                        É possível configurar qualidade e quantidade mínima de imagens capturadas para cadastro, Grava imagens das digitais capturadas para\r\n                        consultas e/ou recadastro, Captura várias imagens para cadastro e seleciona a melhor delas para inclusão, Função Pânico Permite identificar uma\r\n                        digital específica como “função pânico” Desta forma, quando um registro de acesso é feito através desta digital, o sistema pode\r\n                        acionar processos especiais, como acionamento de alarme, envio de e-mail alerta, entre outras ações, Para quem utiliza o reconhecimento de\r\n                        digitais online, ao editar o cadastro de digitais ocorre a sincronização automática entre o sistema local e online.<br>', '<strong>Plantas Baixas: </strong>\r\n                        Permite acompanhar Acessos feitos nos equipamentos, podendo exibir avisos e imagens de câmeras associadas a este equipamento,\r\n                        Status da comunicação dos equipamentos da planta baixa, Imagens de câmeras integradas ao software de CFTV, Avisos de sensores,\r\n                        tais como presença, incêndio, porta aberta, entre outros. Pessoas dentro da planta baixa através do último acesso feito. <br>', '<strong>Benefícios: </strong>\r\n                        Compatível com a maioria dos equipamentos de Controle de Acesso, Captura de Imagens e Acionadores do mercado, Exportação de relatórios\r\n                        para diversos formatos tais como PDF, XPS, Html Word, Excel, BMP, GIF, JPEG, PNG, entre outros. Opção para conexão com banco\r\n                        de dados MS SQL Server, Oracle, entre outros. Projetado para aplicações críticas e grandes quantidades de acessos.\r\n                        Módulo de Controle de Refeitório integrado ao sistema. Ações de acordo com acontecimentos tais como enviar e-mail\r\n                        caso um equipamento deixar de responder, entre outros.', 'contato?assunto=orcamento_acesso', 'Solicitar Orçamento', 1, '2023-04-10 10:37:18', NULL);
INSERT INTO `sts_solutions_che` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `title_left`, `text_left`, `title_center`, `text_center`, `title_right`, `text_right`, `image_center`, `title_image_center`, `text_image_center`, `title_under`, `text_under`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'checkin.jpg', 'Controle de Ponto ou Acesso', 'com mais economia, segurança e versatilidade', 'O Secullum Checkin', 'Foi criado para servir como alternativa à utilização de equipamentos que validam o acesso de pessoas em estabelecimentos que utilizam os sistemas da Secullum.<br><br>\r\n                            O Secullum Checkin é um aplicativo desenvolvido para Tablets, Smartphones e Desktops, com vários recursos para quem busca otimizar o controle de ponto e acesso com um baixo investimento.<br><br>\r\n                            É capaz de identificar pessoas através de reconhecimento facial, senhas numéricas, QR Codes ou outros códigos de barras, inclusive com máscara de proteção e acessórios.', 'contato?assunto=orcamento_checkin', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"300\" src=\"https://www.youtube.com/embed/e5zglRGwAlc\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Configuração simples, utilização <br> mais simples ainda.', 'O aplicativo tem configurações rápida e fácil, <br>levando apenas  alguns minutos para <br>estar pronto para uso, associado ao\r\n                        <br> sistema de controle de acesso da Secullum.', 'Rapidez no reconhecimento facial <br><br>', 'O reconhecimento facial é quase instantâneo <br>  devido ao recurso de BioWeb, <br> serviço dedicado criado exclusivamente <br> para este fim.', 'O melhor custo/benefício <br> <br>', 'O Checkin tem ótimo custo/benefício, pois transforma o <br> Tablet ou Smartphone em um controlador de acessos <br> integrado aos Sistemas Secullum.', 'checkin2.jpg', 'RECONHECIMENTO FACIAL EXCLUSIVO', 'O Secullum Checkin utiliza o Bioweb que é um serviço especialmente criado pela <br>\r\n                                Secullum para o reconhecimento facial e armazenamento de dados. O Bioweb, <br>\r\n                                além de seguro, reconhece instantaneamente o usuário, não importando o tamanho<br> \r\n                                do banco de dados e conta ainda com a vantagem de não haver um limite máximo de faces <br>\r\n                                que podem ser cadastradas. Para utilizar o reconhecimento facial no Secullum Checkin <br>\r\n                                é necessário a contratação do serviço Bioweb.', 'Características do Secullum Checkin', 'Disponível para Tablets e Smartphones com sistema Android ou iOS.   <br>\r\n                        Identifica pessoas através de reconhecimento facial, senha numérica, Código de barras (QR CODE, Código 39, 2 de 5 intercalado ou PDF 417)<br>\r\n                        Aciona, integrado aos softwares da Secullum, fechaduras, catracas, portas, cancelas, entre outros.<br>\r\n                        Mostra a foto do usuário no momento da identificação, juntamente à mensagens personalizadas.<br>\r\n                        Possui modo de operação Offline, que funciona independente de conexão com o banco de dados central ou Internet (exceto no modo de reconhecimento facial e da digital).<br>\r\n                        É ideal para utilização em academias, escolas, eventos e em todas as atividades onde seja necessário agilidade no controle de acesso.<br>\r\n                        Pode ser utilizado para controle de acesso acoplado a cancelas que não possuem placas controladoras.', 'contato?assunto=orcamento_checkin', 'Solicitar Orçamento', 1, '2023-04-12 11:04:37', NULL);
INSERT INTO `sts_solutions_esc` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `sub_title_left`, `text_left`, `sub_title_center`, `text_center`, `sub_title_right`, `text_right`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_image_center`, `text_btn_image_center`, `sub_title_under`, `text_under`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'escola_1.jpg', 'Alunos Mais Seguros', 'Mais controle para os pais e visibilidade para a instituição', 'O Secullum Escola', 'Foi criado para garantir a segurança e acompanhamento dos alunos enquanto gerencia o controle de acesso e\r\n                             centraliza o controle financeiro da sua instituição. <br>\r\n                             Ele conta com recursos de segurança que permitem que pessoas, pré-determinadas pelos responsáveis, \r\n                             possam buscar o aluno, permite receber notificações via App no Smartphone ou Tablet dos horários de entrada\r\n                              e saída, dentre outros.', 'contato?assunto=orcamento_escola', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/SnYIh4z0CIY\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Secullum Escola App', 'É um aplicativo direcionado para instituições que procuram oferecer mais praticidade e segurança aos seus clientes.\r\n                        Ele trabalha em sincronia com o Secullum Escola e oferece uma ótima experiência para os pais e responsáveis que gostam de ter informações sempre na palma da mão.\r\n                        Através dele é possível acompanhar entradas e saídas, dados cadastrais, entre outras funções.', 'Gestão Completa e Centralizada', 'O Secullum Escola possibilita a gestão de contas a pagar e receber, geração de mensalidades, \r\n                        controle de acesso de inadimplentes, dentre outros recursos.', 'Secullum Escola', 'O aplicativo do Secullum Escola mantém pais e responsáveis informados sobre entradas e saídas dos alunos \r\n                        das instituições de ensino, garantindo total controle sobre os acessos. \r\n                        Todas as vezes que o aluno faz alguma entrada ou saída na instituição, uma notificação é enviada para o responsável. \r\n                        É mais segurança para pais e alunos!', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e \r\n                            <br>Smartphone, completo para quem procura otimizar o <br>\r\n                            controle de acesso com um baixo investimento.', 'soluche', 'Saiba Mais', 'Características do Secullum Escola', 'Compatível com a maioria dos equipamentos de Controle de Acesso, Captura de Imagens e Acionadores do mercado. <br>\r\n                        Cadastro de alunos, professores, funcionários e qualquer outro tipo de pessoa. <br>\r\n                        Opção para bloqueio de inadimplentes e registro de ocorrências.<br>\r\n                        Controle de serviços ou cursos oferecidos com geração de mensalidades. <br>\r\n                        Gestão de contas a pagar e receber com opções para geração de recibos, boletos e controle de cheques. <br>\r\n                        Cadastro e controle de Turmas com limites de pessoas. <br>\r\n                        Módulo de consumo para lancherias, vendas de material didático e controle básico de estoque.<br>\r\n                        Tela de Acompanhamento de Acessos, Tela de Cadastro de Ocorrências, Tela de Serviços, Tela de Contas a Pagar, Tela de Controle de Frequência\r\n                        Tela de Cadastro de Turmas. <br>\r\n                        Várias Opções de Exportação de Relatórios.', 'contato?assunto=orcamento_escola', 'Solicitar Orçamento', 1, '2023-04-10 16:34:20', NULL);
INSERT INTO `sts_solutions_pont` (`id`, `image_top`, `title_image`, `test_image`, `url_btn_image`, `text_tbn_image`, `sub_title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `sub_title_left_1`, `text_left_1`, `sub_title_center_1`, `text_center_1`, `sub_title_right_1`, `text_right_1`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_center`, `text_btn_center`, `video_center`, `sub_title_2`, `text_under_1`, `text_under_2`, `text_under_3`, `text_under_4`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'ponto.jpg', 'Nunca Foi Tão Fácil', 'Gerenciar o Cartão de Ponto', 'https://pontoweb.secullum.com.br/login', 'Acessar o Secullum Ponto Web', 'O Secullum Ponto Web', 'É um sistema de ponto 100% Web e totalmente seguro, criado para facilitar o dia a dia dequem precisa gerenciar a folha de ponto e não tem tempo a perder.\r\nCom ele você cria e configura rapidamente turnos, escalas e banco de horas, controla a jornada de colaboradores externos ou em home office, envia a folha de ponto para assinatura remotamente, gerencia a folha de ponto de várias filiais de um único lugar, acompanha tudo em tempo real por indicadores, visualiza rapidamente a decomposição de cálculos, pode utilizar o recurso de geolocalização no registro de ponto, pode utilizar o recurso de reconhecimento facial, pode receber atestados pelo App, emitir relatórios personalizados e muito mais.', 'contato?assunto=orcamento_ponto', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/fsxXND7IIhk\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Gerenciamento Remoto', 'Com o Ponto Web é possível gerenciar registros de ponto, aprovar solicitações e muito mais, quando e de onde você quiser, sem a necessidade de estar presencialmente na empresa.', 'Registro de Ponto por Smartphone', 'Registro, justificativas, ajustes, envio de atestado e assinatura do cartão de ponto na palma da mão. Experimente o App Central do Funcionário. Disponível para Android e IOS.', 'Integração Fácil', 'Compatível com a maioria das marcas de Relógios de Ponto e diversos Sistemas de Folha, a Integração do Secullum vira uma tarefa descomplicada.', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e <br>Smartphone, completo para quem procura otimizar o <br>controle de acesso com um baixo investimento. <br><br>É capaz de identificar pessoas através de <br> Reconhecimento Facial, Senhas Numéricas ou QR Code.', 'soluche', 'Saiba Mais', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/h5hTIMKik0g\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Algumas Funcionalidades do Secullum Ponto Web', '<strong>Horários: </strong> Opções de cálculos compensado, almoço livre e neutro, Controle de fechamento, \r\n                    Cálculo tempo +/- na carga horária de dia e funcionário específico, Controle de dias de extras e folgas,\r\n                    Tolerâncias de cálculo conforme artigo 58, Configuração manual de tolerâncias, Controle de batidas pré-assinaladas, \r\n                    Separar horas noturnas de normais, Escalas cíclicas e mensais, Banco de horas, Exibir coluna com horas de repouso \r\n                    faltantes em trabalho contínuo (CLT - Art. 71), Ponto por exceção Inter jornada, Horários de Descanso, Controle de horas,\r\n                    DSR automático e variável, Opção para incluir feriados como descanso, Horários Extras, Controle de extras normais, \r\n                    Controle de extras noturnas e intervalos, Opções de acúmulos de horas, Divisões de faixas.', '<strong>Cadastros:</strong> Diversos, Cidades, Departamentos, Empresas, Feriados e ponto facultativo, Funções,\r\n                    Motivos de demissão, 4 horários por funcionário,  Separar somatória de extras após a meia noite<br>\r\n                    <strong> Equipamentos: </strong> Dados cadastrais, Configurações, Manutenção de biometrias, Agendamento de comunicações Status das bobinas de impressão.<br>\r\n                    <strong> Usuários: </strong> Dados cadastrais,  Restrições aos menus, Restrições aos menus por empresas ou departamentos, Log de informações,\r\n                    Replicar restrições aos menus, Perguntas adicionais,  Colunas mix, Alterações em massa, Rodapé do cartão ponto.<br>\r\n                    <strong>Cartão Ponto:</strong> Visualização/alteração das batidas de ponto, Cadastro de observações, \r\n                    Ajustes parciais, Solicitações Web, Aceitar/descartar alterações de ponto.<br>', '<strong>Relatórios: </strong> Diversos, Geração de arquivos fiscais AFDT/AFCEF, Listagem de funcionários, \r\n                    Listagem de horários Distribuição de horários, Quadro de horários, Números provisórios, Solicitações web, Ocorrências,\r\n                    Batidas rejeitadas, Afastamentos, Exportar para PDF, HTML, Word, Excel, Extratos do banco de horas.<br>\r\n                    <strong>Cálculos: </strong>  Impressão de cartão ponto, Extrato de totais e espelho de ponto Port. 1510, \r\n                    Exportação de arquivos texto de cálculos, Assinatura eletrônica do espelho de ponto, \r\n                    Ponto Diário, Visualização/alteração das batidas de ponto. <br>\r\n                    <strong>Comunicação: </strong> Receber arquivo AFD/Texto, Enviar e receber via rede,  Comunicação online, \r\n                    Exportação de Dados, Layouts de exportação de cálculos Serviços Online, Módulo Web Na Nuvem. <br>', '<strong>Central do Funcionário - App: </strong> Ponto diário, Inclusão de ponto manual, Notificar no momento de incluir\r\n                    o ponto, Justificativas, Inclusão de ponto manual por QR Code (checkin), Incluir o ponto através de Reconhecimento\r\n                    Facial(checkin), Alterar batidas, Fazer solicitações , Inclusão de ponto off-line, Visualizar eventos,\r\n                    Tirar foto ao incluir ponto, Agendamento de tarefas (e-mails e relatórios), Envio de mensagens , \r\n                    Inclusão de Ponto Manual por perímetro, Dados cadastrais, Alterar senha, Gestor efetua correções/aceites, \r\n                    Cartão ponto, Envio de e-mail comprovante de inclusão manual, Exportar cartão ponto completo em PDF, Indicadores\r\n                    personalizados na tela inicial', 'contato?assunto=orcamento_ponto', 'Solicitar Orçamento', 1, '2023-04-09 11:10:02', NULL);
INSERT INTO `sts_solution_clu` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `sub_title_left`, `text_left`, `sub_title_center`, `text_center`, `sub_title_right`, `text_right`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_image_center`, `text_btn_image_center`, `sub_title_under`, `text_under`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'clube.jpg', 'Gestão Completa e Automatizada', 'para clubes e associações', 'O Secullum Clube', 'É repleto de vantagens e tem um ótimo custo-benefício para a gestão de associações, agremiações, federações, companhias e clubes. <br>\n                            Com interface simples, o Secullum Clube permite que você centralize o controle de acesso e financeiro da instituição, \n                            tornando-se a ferramenta perfeita para gerenciar o controle de titulares, dependentes, funcionários e visitantes, enquanto garante \n                            um ambiente mais organizado e seguro. <br>\n                            Toda a gestão pode ser automatizada e possui diversos recursos como administração de contas a pagar, \n                            contas a receber, alerta de vencimentos, bloqueio de inadimplentes e módulo de consumo extras.', 'contato?assunto=orcamento_clube', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/ugbt6miJsP8\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>\n                    ', 'Automatize processos, economize tempo', 'Realize cobranças através de integração com bancos e instituições financeiras através de arquivos de texto. <br>\n                       Importe arquivos de quitação de boletos, entre outros. ', 'Mantenha mensalidades em dia', 'Controle as contas e mensalidades dos titulares e dependentes através dos diversos recursos de bloqueios e avisos. <br>\n                        Utilize os relatórios para acompanhar os inadimplentes, enviar e-mails de cobranças, entre outros.', 'Um único software, diversas possibilidades', 'Adapte facilmente o software para qualquer porte de clube ou associação, através de opções práticas e completas, \n                        tais como criação de relatórios personalizados, renomeação de campos nos cadastros, entre outros.', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e \r\n                            <br>Smartphone, completo para quem procura otimizar o <br>\r\n                            controle de acesso com um baixo investimento. \r\n                            <br><br>\r\n                            É capaz de identificar pessoas através de <br> Reconhecimento Facial, Senhas Numéricas ou QR Code.\r\n                            ', 'soluche', 'Saiba Mais', 'Características do Secullum Clube', 'Cadastro de Titulares, Dependentes, Funcionários, Visitantes e quaisquer tipos de pessoas envolvidas com o clube. <br>\n                        Cadastro e controle de Turmas com limites de pessoas.\n                        Impressão de Etiquetas para Malas Diretas com layout totalmente configurável.<br>\n                        Controle de Ocorrências e acompanhamento da Planta Baixa do clube em tempo real.\n                        Controle de Produtos e Serviços oferecidos e geração de mensalidades.<br>\n                        Gestão de Contas a Pagar com controle de gastos previstos e realizados.\n                        Gestão de Contas a Receber com opções para geração de Recibos e controles de Cheques.<br>\n                        Relatórios de Fluxo de Caixa previstos, realizados e gráfico de evolução periódico. \n                        Impressão de Boletos e integração com bancos.<br>\n                        Tela especial simplificada para quitação de mensalidades e pesquisa de informações de pessoas.', 'contato?assunto=orcamento_clube', 'Solicitar Orçamento', 1, '2023-04-11 11:36:51', '2023-04-11 11:36:51');
INSERT INTO `sts_solution_est` (`id`, `image_top`, `title_image_top`, `text_image_top`, `title_1`, `text_1`, `url_btn_1`, `text_btn_1`, `video_1`, `sub_title_left`, `text_left`, `sub_title_center`, `text_center`, `sub_title_right`, `text_right`, `image_center`, `title_image_center`, `text_image_center`, `url_btn_image_center`, `text_btn_image_center`, `sub_title_under`, `text_under`, `url_btn_under`, `text_btn_under`, `sts_situation`, `created`, `modified`) VALUES
(1, 'estacionamento.jpg', 'Gestão de Estacionamento', 'de forma Fácil e Descomplicada', 'O Secullum Estacionamento', 'A gestão de estacionamento nunca foi tão simples e completa. O Secullum Estacionamento centraliza gestão de acesso,\r\n                            gestão financeira, controle de lotação e cobrança. <br> <br>\r\n                            O Secullum Estacionamento pode ser adaptado para ambientes de qualquer tamanho. Com um sistema completo, \r\n                            feito para atender todas as demandas de seu negócio, você tem total controle sobre tudo que acontece, \r\n                            desde o acesso de veículos, com controle por totens e cancelas, até a gestão de ocorrências.', 'contato?assunto=orcamento_estacionamento', 'Solicitar Orçamento', '<iframe width=\"500\" height=\"350\" src=\"https://www.youtube.com/embed/dqHZL9yNf60\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>', 'Controle facilmente o fluxo de pessoas e vagas', 'Através de uma cadastro descomplicado, controle tanto mensalistas quanto periodistas, acompanhando suas contas e \r\n                       bloqueando inadimplentes. <br>\r\n                       Controle a quantidade de vagas para qualquer tipo de pessoa.', 'Personalize tabelas de preços e modos de cobrança', 'Monte sua planilha de preços através de diversas opções de cobranças, separando por tipos de veículos e faixas horárias.\r\n                        <br>\r\n                        Utilize opções especiais para cobranças de convênios e gratuidades.', 'Mantenha os caixas controlados', 'Utilize o controle de caixas para acompanhar os pagamentos, registrar a entrada e saída de dinheiro,\r\n                         imprimir recibos, entre outros, através de uma tela simples e configurável.', 'checkin2.jpg', 'INTEGRAÇÃO COM SECULLUM CHECKIN', 'O Secullum Checkin é um aplicativo para Tablets e \r\n                            <br>Smartphone, completo para quem procura otimizar o <br>\r\n                            controle de acesso com um baixo investimento. \r\n                            <br><br>\r\n                            É capaz de identificar pessoas através de <br> Reconhecimento Facial, Senhas Numéricas ou QR Code.', 'secullum_checkin.php', 'Saiba Mais', 'Características do Secullum Estacionamento', '<strong>Gestão de estacionamentos descomplicada </strong> \r\n                        Um estacionamento tem como princípios fundamentais a segurança, o controle do tempo que um veículo ocupa \r\n                        a vaga e uma gestão financeira eficiente.\r\n                        Quer saber como você pode ter essas três coisas em uma única solução? <br><br>\r\n                        Com o <strong> Secullum Estacionamento </strong>. <br><br>\r\n                        O Secullum Estacionamento pode ser adaptado para ambientes de qualquer tamanho. <br>\r\n                        Com um sistema completo, feito para atender todas as demandas que o seu negócio, \r\n                        você tem total controle sobre tudo que acontece,\r\n                        desde o acesso de veículos, com controle por totens e cancelas, até a gestão de ocorrências. <br>\r\n                        Para facilitar, também é possível gerenciar tudo a partir de tablets e smartphones, sem a necessidade de equipamentos \r\n                        de controle de acesso.', 'contato?assunto=orcamento_estacionamento', 'Solicitar Orçamento', 1, '2023-04-11 10:51:36', NULL);
